=SELECT * FROM match_documents(
  '[{{ $json.data[0].embedding.join(",") }}]'::vector,
  {{ $node["Webhook RAG Query"].json.body.match_threshold || 0.75 }},
  {{ $node["Webhook RAG Query"].json.body.match_count || 5 }},
  {{ $node["Webhook RAG Query"].json.body.category ? "'" + $node["Webhook RAG Query"].json.body.category + "'" : "NULL" }}
)

parameters.functionCode:
// Format results for AI Agent consumption
const items = $input.all();
const webhookData = $node["Webhook RAG Query"].json.body;

if (!items || items.length === 0) {
  return [{
    json: {
      success: false,
      results: [],
      context: "",
      metadata: {
        query: webhookData.query_text,
        total_results: 0,
        category_filter: webhookData.category || null,
        match_threshold: webhookData.match_threshold || 0.75
      },
      message: "Nenhum conhecimento relevante encontrado"
    }
  }];
}

// Process and structure results
const results = items.map((item, index) => ({
  rank: index + 1,
  content: item.json.content,
  source: {
    file: item.json.source_file,
    category: item.json.category,
    id: item.json.id
  },
  similarity: parseFloat(item.json.similarity).toFixed(3),
  relevance: item.json.similarity > 0.85 ? "high" : item.json.similarity > 0.75 ? "medium" : "low"
}));

// Create context string for AI (combines top results)
const context = results
  .slice(0, 3) // Top 3 most relevant
  .map((r, idx) => `[Fonte ${idx + 1} - ${r.source.file} (${(parseFloat(r.similarity) * 100).toFixed(1)}% similar)]\n${r.content}`)
  .join("\n\n---\n\n");

// Calculate average similarity
const avgSimilarity = results.reduce((acc, r) => acc + parseFloat(r.similarity), 0) / results.length;

return [{
  json: {
    success: true,
    results: results,
    context: context,
    metadata: {
      query: webhookData.query_text,
      total_results: results.length,
      average_similarity: avgSimilarity.toFixed(3),
      category_filter: webhookData.category || null,
      match_threshold: webhookData.match_threshold || 0.75,
      categories_found: [...new Set(results.map(r => r.source.category))],
      files_found: [...new Set(results.map(r => r.source.file))]
    },
    usage: {
      description: "Use 'context' field to inject into AI prompt as knowledge base",
      example: `Baseado no seguinte conhecimento da E2 Soluções:\n\n${context.substring(0, 200)}...`
    }
  }
}];
